import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function ProductDetails() {
  const api_url = 'https://fakestoreapi.com/products';
  const [product, setProduct] = useState({});
  const params = useParams();

  useEffect(() => {
    fetch(`${api_url}/${params.productId}`)
      .then((res) => res.json())
      .then((productData) => setProduct(productData))
      .catch((error) => console.error("Error fetching product:", error));
  }, [params.productId]);

  // Ensure product title exists before rendering
  const productTitle = product.title ? product.title : "Loading...";

  return (
    <>
      <h1 className="text-center">Product Details Page</h1>
      <h3>Product ID: {params.productId}</h3>
      <h5>Product Title: {productTitle}</h5>
    </>
  );
}

export default ProductDetails;
