import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import './Product.css'


function ProductDetails() {

        const api_url = 'https://fakestoreapi.com/products';
        const [product, setProduct] = useState({});
        const params = useParams();


        useEffect (()=> {
                fetch( `${api_url}/${params.productId}` )
                .then((res) => res.json())
                .then((product)=> setProduct(product)) ;
        }, []);

        return(<>
                <div className="container"> 
                        <h1 className="text-center"> 
                                Product Details Page
                        </h1>        

                        <h3>  ID : {params.productId} </h3>
                        <img className='PImage' src={product.image}/> 
                        <h5 className="productData success-border-subtle">  Title : {product.title} </h5>
                        <h5 className="productData">  Price : {product.price}$ </h5>
                        <h5 className="productData">  Category : {product.category} </h5>
                        <p className='PDescription productData'> Description : {product.description} </p>  
                        <button className="btn btn-success"> Add to cart .. </button>
                </div> 
                
        </>);
}

export default ProductDetails;
